import React from "react";
import BabyNamesData from "./babyNamesData.json";

const Search = (props) => {
  const setBabyNames = props.setBabyNames;
  const handelSearch = (event) => {
    //when entering any letter I want to take that letter and filter my names based on that name
    //useState for changing the names
    setBabyNames(
      BabyNamesData.filter(
        (baby) => baby.name.indexOf(event.target.value) !== -1
      )
    );
  };

  return (
    <div class="search">
      <input id="search-id" type="text" onChange={handelSearch} />
      <button>Search</button>
    </div>
  );
};

export default Search;
